import React, { useState, useRef, useEffect } from "react";

function French() {

    const [message, setMessage] = useState('');
    const [responses, setResponses] = useState([]);
    const conversationRef = useRef(null);

    const handleSubmit = (e) => {
        e.preventDefault();
        fetch('http://localhost:3001/French', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ message }),
        })
            .then((res) => res.json())
            .then((data) => {
                setResponses([...responses, { message, response: data.message }]);
            });
        setMessage('');
    };

    useEffect(() => {
        conversationRef.current.scrollTop = conversationRef.current.scrollHeight;
    }, [responses]);

    return (
        <div className="French p-4 sm:ml-64 static">
            <div className="flex h-full flex-col">
                <div className="flex justify-center">
                    <img src={require('../assets/French.png')} className={"h-64 rounded-lg"} />
                </div>
                <div className="absolute z-20 bottom-0 w-5/6 py-4">
                    <form onSubmit={handleSubmit} className={"flex justify-center"}>
                        <textarea value={message} placeholder="Enter your question " onChange={(e) => setMessage(e.target.value)} type="text" id="large-input" row="50" col="50" className="w-full mt-4 mr-4 h-20 border border-gray-300 rounded p-2" style={{ position: 'relative', bottom: 0 }}></textarea>
                        <button type="submit" className={"hover:bg-blue-700  text-white font-bold py-2 px-4 rounded-lg transition-all duration-200 active:bg-blue-700 focus:outline-none focus:ring-blue-500 "}>
                            envoyer
                        </button>
                    </form>
                </div>
                <div className="w-full md:h-96 xl:max-h-full overflow-y-scroll"  ref={conversationRef}>
                    {responses.map((conversation, index) => (
                        <div className="flex flex-col" key={index}>
                            <div className="flex justify-end">
                                <div className="flex items-center w-full max-w-xs p-4 space-x-4 text-gray-500 bg-white divide-x divide-gray-200 rounded-lg shadow top-20 right-5 dark:text-gray-400 dark:divide-gray-700 space-x dark:bg-gray-800">
                                    <div className="text-sm font-normal">Me : {conversation.message}</div>
                                </div>
                            </div>
                            <div className="flex justify-start">
                                <div className="flex items-center w-full max-w-xs p-4 space-x-4 text-white rounded-lg shadow bg-gray-700 top-15 left-5">
                                    <img src={require('../assets/FrenchTeacher.png')} className="w-10 h-10 rounded-full" alt="Rounded avatar" />
                                    <div className="text-start text-sm font-normal">Madame Dupond : {conversation.response}</div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default French;
