import React, { useState, useRef, useEffect } from "react";

function French() {
    const [message, setMessage] = useState("");
    const [responses, setResponses] = useState([]);
    const messagesEndRef = useRef(null);

    const scrollToBottom = () => {
        messagesEndRef.current.scrollIntoView({ behavior: "smooth" });
    };

    useEffect(scrollToBottom, [responses]);

    const handleSubmit = (e) => {
        e.preventDefault();
        fetch("http://localhost:3001/French", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({ message }),
        })
            .then((res) => res.json())
            .then((data) => {
                setResponses([...responses, { message, response: data.message }]);
                setMessage("");
            });
    };

    return (
        <div className="French p-4 sm:ml-64 static">
            <div className="flex h-full flex-col ">
                <div className=" flex justify-center ">
                    <img src={require("../assets/French.png")} className={"h-64"} />
                </div>
                <div className="absolute z-20 bottom-0 w-full  p-4">
                    <form onSubmit={handleSubmit} className={"flex"}>
            <textarea
                value={message}
                placeholder="Enter your question "
                onChange={(e) => setMessage(e.target.value)}
                type="text"
                id="large-input"
                row="50"
                col="50"
                className="w-3/4 mt-4 h-20 border border-gray-300 rounded p-2"
            ></textarea>
                        <button
                            type="submit"
                            className={
                                " hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full transition-all duration-200 active:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50 focus:transform focus:scale-110 relative"
                            }
                        >
              <span className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
                <svg
                    className="h-4 w-4 text-white"
                    xmlns="http://www.w3.org/2000/svg"
                    viewBox="0 0 20 20"
                    fill="currentColor"
                ></svg>
              </span>
                        </button>
                    </form>
                </div>
                <div className="flex h-full flex-col relative conversation-container pb-20">
                    {responses.map((conversation, index) => (
                        <div className={""} key={index}>
                            <div
                                id="toast-top-right"
                                style={{ marginTop: index * 15 + "%" }}
                                className="flex items-center w-full max-w-xs p-4 space-x-4 text-gray-500 bg-white divide-x divide-gray-200 rounded-lg shadow top-20 right-5 dark:text-gray-400 dark:divide-gray-700 space-x dark:bg-gray-800 absolute"
                                role="alert"
                            >
                                <div className="text-sm font-normal">Me : {conversation.message}</div>
                            </div>
                            <div
                                id="toast-top-left"
                                style={{ marginTop: index * 15 + "%" }}
                                className="flex items-center w-full max-w-xs p-4 space-x-4 text-white  rounded-lg shadow  bg-gray-700 top-15 left-5 absolute"
                                 role="alert">
                                <img src={require('../assets/FrenchTeacher.png')} className="w-10 h-10 rounded-full" alt="Rounded avatar"></img>
                                <div className="text-start text-sm font-normal">Madame Dupond : {conversation.response}</div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}
export default French;
