import React, { useState } from "react";

function French() {

    const [message, setMessage] = useState('');
    const [responses, setResponses] = useState([]);
    const handleSubmit = (e) => {
        e.preventDefault();
        fetch('http://localhost:3001/French', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ message }),
        })
            .then((res) => res.json())
            .then((data) => {
                setResponses([...responses, { message, response: data.message }]);
            });
    };

    return (
        <div className="French p-4 sm:ml-64 static">
            <div className="flex h-full flex-col">
                <div className="flex justify-center">
                    <img src={require('../assets/French.png')} className="h-64" />
                </div>
                <div className="conversation-container flex-1 overflow-y-auto p-4">
                    {responses.map((conversation, index) => (
                        <div className="" key={index}>
                            <div
                                id="toast-top-right"
                                style={{ marginTop: index * 15 + '%' }}
                                className="flex items-center w-full max-w-xs p-4 space-x-4 text-gray-500 bg-white divide-x divide-gray-200 rounded-lg shadow top-20 right-5 dark:text-gray-400 dark:divide-gray-700 space-x dark:bg-gray-800 absolute"
                                role="alert"
                            >
                                <div className="text-sm font-normal">Me: {conversation.message}</div>
                            </div>
                            <div
                                id="toast-top-left"
                                style={{ marginTop: index * 15 + '%' }}
                                className="flex items-center w-full max-w-xs p-4 space-x-4 text-white rounded-lg shadow bg-gray-700 top-15 left-5 absolute"
                                role="alert"
                            >
                                <img
                                    src={require('../assets/FrenchTeacher.png')}
                                    className="w-10 h-10 rounded-full"
                                    alt="Rounded avatar"
                                />
                                <div className="text-start text-sm font-normal">
                                    Madame Dupond: {conversation.response}
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
                <form onSubmit={handleSubmit} className="flex flex-row items-center">
      <textarea
          value={message}
          placeholder="Enter your question"
          onChange={(e) => setMessage(e.target.value)}
          type="text"
          id="large-input"
          row="50"
          col="50"
          className="flex-1 border border-gray-300 rounded p-2"
      ></textarea>
                    <button
                        type="submit"
                        className="ml-2 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg transition-all duration-200 active:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50 focus:transform focus:scale-110 relative"
                    >
                        Envoyer
                    </button>
                </form>
            </div>
        </div>

    );
}
export default French;
