import React, { useState } from "react";

function Favoris(props) {
    // Etat local pour stocker si le produit est dans les favoris
    const [favori, setFavori] = useState(false);

    // Fonction appelée lorsque l'utilisateur clique sur le bouton d'ajout aux favoris
    function toggleFavori() {
        // Mettre à jour l'état local
        setFavori(!favori);

        // Récupérer la liste des favoris depuis le localStorage
        let favoris = JSON.parse(localStorage.getItem("favoris")) || [];

        // Si le produit est déjà dans les favoris, le supprimer
        if (favori) {
            favoris = favoris.filter(p => p.id !== props.produit.id);
        } else {
            // Si le produit n'est pas dans les favoris, l'ajouter
            favoris.push(props.produit);
        }

        // Enregistrer la liste des favoris mise à jour dans le localStorage
        localStorage.setItem("favoris", JSON.stringify(favoris));
    }

    return (
        <div className={'ml-52'}>
            <h2>{props.produit.nom}</h2>
            <p>{props.produit.description}</p>
            <button onClick={toggleFavori}>
                {favori ? "Retirer des favoris" : "Ajouter aux favoris"}
            </button>
        </div>
    );
}

export default Favoris;
