import React from "react";

function Favoris({ produit }) {
    const handleAjouterAuxFavoris = () => {
        // Récupérer la liste des favoris depuis le localStorage
        const favoris = JSON.parse(localStorage.getItem("favoris")) || [];

        // Vérifier si le produit est déjà dans les favoris
        const existeDeja = favoris.some(p => p.id === produit.id);

        // Ajouter le produit aux favoris s'il n'existe pas déjà
        if (!existeDeja) {
            favoris.push(produit);
            localStorage.setItem("favoris", JSON.stringify(favoris));
        }
    };

    return (
        <div className={"ml-52"}>
        <button onClick={handleAjouterAuxFavoris}>
            Ajouter aux favoris
        </button>
            </div>
    );
}

export default Favoris;
