import React, { useState } from "react";
import '../App.css';

function French() {
    const [message, setMessage] = useState('');
    const [responses, setResponses] = useState([]);
    const handleSubmit = (e) => {
        e.preventDefault();
        fetch('http://localhost:3001/French', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ message }),
        })
            .then((res) => res.json())
            .then((data) => {
                setResponses([...responses, { message, response: data.message }]);
            });
    };

    return (
        <div className="French p-4 sm:ml-64 relative">
            <div className=" flex justify-center ">
                <img src={require('../assets/FrenchTeacher.png')} className={"h-64"} />
            </div>
            <form onSubmit={handleSubmit} className={""}>
                <div className="flex  flex-col items-center">
                    <label htmlFor="large-input" className="block mb-2 text-sm font-medium font-bold text-2xl text-white ">Hello I m Madame Dupond Ask me question </label>
                    <textarea  value={message}
                            placeholder="Enter your question "
                            onChange={(e) => setMessage(e.target.value)}
                            type="text" id="large-input" row = "50" col = "50"
                            className=" mb-6 block p-2.5 w-1/2 text-md text-white bg-gray-50 rounded-lg border border-gray-300 focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"></textarea>
                </div>
                <button className={"px-5 py-3 text-base font-medium text-center text-white bg-blue-700 rounded-lg hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"} type="submit">Send</button> {/* Utilisez type="submit" pour soumettre le formulaire */}
            </form>
            {responses.map((conversation, index) => (
                <div className={"relative"} key={index}>
                    <div id="toast-top-left"
                         style={{marginTop: index * 15 + '%'}}
                         className="flex items-center w-full max-w-xs p-4 space-x-4 text-white  rounded-lg shadow  bg-gray-700 top-15 left-5 absolute"
                         role="alert">
                        <img src={require('../assets/FrenchTeacher.png')} className="w-10 h-10 rounded-full" alt="Rounded avatar"></img>
                        <div className="text-start text-sm font-normal">Madame Dupond : {conversation.response}</div>
                    </div>
                    <div id="toast-top-right"
                         style={{marginTop: index * 15 + '%'}}
                         className="flex items-center w-full max-w-xs p-4 space-x-4 text-gray-500 bg-white divide-x divide-gray-200 rounded-lg shadow top-20 right-5 dark:text-gray-400 dark:divide-gray-700 space-x dark:bg-gray-800 absolute"
                         role="alert">

                        <div className="text-sm font-normal">Me : {conversation.message}</div>
                    </div>
                </div>
            ))}
        </div>
    );
}
export default French;
