import React from 'react';
import { UserAuth } from '../context/AuthContext';
import {clear} from "@testing-library/user-event/dist/clear";
import { useNavigate } from 'react-router-dom';
const Account = () => {
  const { logOut, user } = UserAuth();

  const handleSignOut = async () => {
    try {
      await logOut();
      clear.localState();
        // eslint-disable-next-line react-hooks/rules-of-hooks
        useNavigate('/')
    } catch (error) {
      console.log(error);
    }

  };


    const renderBadge = () => {
        if (user) {
            const signUpDate = new Date(user.metadata.creationTime);
            const monthsSinceSignUp = Math.floor((new Date() - signUpDate) / (1000 * 60 * 60 * 24 * 30));

            if (monthsSinceSignUp >= 4) {
                return <div className="badge">Badge des 4 mois</div>;
            }
        }
        return null;
    };


    return (
    <div className='flex flex-col justify-center items-center'>
        <div className=" mt-24 w-full h-full max-w-sm bg-white border border-gray-200 rounded-lg shadow dark:bg-gray-800 ">
            <div className="flex justify-end px-4 pt-4">
                <button id="dropdownButton" data-dropdown-toggle="dropdown"
                        className="inline-block text-gray-500 dark:text-gray-400 hover:bg-gray-100 dark:hover:bg-gray-700 focus:ring-4 focus:outline-none focus:ring-gray-200 dark:focus:ring-gray-700 rounded-lg text-sm p-1.5"
                        type="button">
                    <span className="sr-only">Open dropdown</span>
                    <svg className="w-6 h-6" aria-hidden="true" fill="currentColor" viewBox="0 0 20 20"
                         xmlns="http://www.w3.org/2000/svg">
                        <path
                            d="M6 10a2 2 0 11-4 0 2 2 0 014 0zM12 10a2 2 0 11-4 0 2 2 0 014 0zM16 12a2 2 0 100-4 2 2 0 000 4z"></path>
                    </svg>
                </button>
                <div id="dropdown"
                     className="z-10 hidden text-base list-none bg-white divide-y divide-gray-100 rounded-lg shadow w-44 dark:bg-gray-700">
                    <ul className="py-2" aria-labelledby="dropdownButton">
                        <li>
                            <a href="#"
                               className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 dark:hover:bg-gray-600 dark:text-gray-200 dark:hover:text-white">Edit</a>
                        </li>
                        <li>
                            <a href="#"
                               className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 dark:hover:bg-gray-600 dark:text-gray-200 dark:hover:text-white">Export
                                Data</a>
                        </li>
                        <li>
                            <a href="#"
                               className="block px-4 py-2 text-sm text-red-600 hover:bg-gray-100 dark:hover:bg-gray-600 dark:text-gray-200 dark:hover:text-white">Delete</a>
                        </li>
                    </ul>
                </div>
            </div>
            <div className="flex flex-col items-center pb-10">
                <img className="w-24 h-24  rounded-full shadow-lg" src={user?.photoURL} />
                <h5 className="mb-1 text-xl font-medium text-gray-900 dark:text-white">{user?.displayName}</h5>
                <span className="text-sm text-gray-500 dark:text-gray-400"></span>
                <div className="flex mt-4 space-x-3 md:mt-6">
                    <a href="#"
                       className="inline-flex items-center px-4 py-2 text-sm font-medium text-center text-white bg-blue-700 rounded-lg hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">Add
                        friend</a>
                </div>
                <button onClick={handleSignOut} className='border hover:bg-white text-white bg-black hover:text-black rounded-lg px-6 py-1 mt-10'>
                    Logout
                </button>
                <div className="bg-gray-100 rounded-lg p-4">
                <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800 mr-2">
                        Nouveau
                      </span>
                    <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800">Promotion
                </span>
                </div>
            </div>
        </div>
    </div>
  );
};

export default Account;
