import React, { useState } from "react";

function Spanish() {
    const [message, setMessage] = useState('');
    const [responses, setResponses] = useState([]);
    const handleSubmit = (e) => {
        e.preventDefault();
        fetch('http://localhost:3001/Spanish', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ message }),
        })
            .then((res) => res.json())
            .then((data) => {
                setResponses([...responses, { message, response: data.message }]);
            });
    };

    return (
        <div className="Spanish">
            <h1>Tinder Seducer</h1>
            <form onSubmit={handleSubmit}>
                <div className="mb-6">
                    <label htmlFor="large-input" className="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Spanish</label>
                    <input  value={message}
                            placeholder="Enter your message my snakebite"
                            onChange={(e) => setMessage(e.target.value)}
                            type="text" id="large-input"
                            className="block w-full p-4 text-gray-900 border border-gray-300 rounded-lg bg-gray-50 sm:text-md focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"></input>
                </div>
                <button submit>Send</button>
            </form>
            {responses.map((conversation, index) => (
                <div key={index}>
                    <div>You: {conversation.message}</div>
                    <div>Response: {conversation.response}</div>
                </div>
            ))}

        </div>
    );
}
export default Spanish;
