import React, { useState } from "react";

function Favoris(props) {
    const [favori, setFavori] = useState(false);
    function toggleFavori() {
        setFavori(!favori);
        let favoris = JSON.parse(localStorage.getItem("favoris")) || [];

        if (favori) {
            favoris = favoris.filter(p => p.id !== props.produit.id);
        } else {
            favoris.push(props.produit);
        }
        localStorage.setItem("favoris", JSON.stringify(favoris));
    }

    return (
        <div className={'ml-52'}>
            <h2>{props.produit.nom}azkl,ea,zkeakz,eae</h2>
            <p>{props.produit.description}akl,ea,zkek,aze,ka</p>
            <button onClick={toggleFavori}>
                {favori ? "Retirer des favoris" : "Ajouter aux favoris"}
            </button>
        </div>
    );
}

export default Favoris;
